// src/components/Main/index.jsx

import React, { useState } from "react";
import styles from "./styles.module.css";
import HelloWorldComponent from "../HelloWorldComponent"; // Import the HelloWorldComponent
import SecretForm from "../SecretForm"; // Import the SecretForm component

const Main = () => {
  const [isHelloWorldOpen, setHelloWorldOpen] = useState(false);
  const [isSecretFormOpen, setSecretFormOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const handleHelloWorldTabClick = () => {
    setHelloWorldOpen(!isHelloWorldOpen);
    setSecretFormOpen(false); // Close the SecretForm when opening HelloWorldComponent
  };

  const handleSecretFormTabClick = () => {
    setSecretFormOpen(!isSecretFormOpen);
    setHelloWorldOpen(false); // Close HelloWorldComponent when opening the SecretForm
  };

  console.log("isHelloWorldOpen:", isHelloWorldOpen);
  console.log("isSecretFormOpen:", isSecretFormOpen);

  return (
    <div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>SECRET.DO</h1>
        <button className={styles.white_btn} onClick={handleLogout}>
          Logout
        </button>
      </nav>

      <div className={styles.centered_content}>
        <p className={styles.static_message}>Discover and Share Secrets</p>
        <div className={styles.centered_buttons}>
          <button className={styles.green_btn} onClick={handleSecretFormTabClick}>
            Publish Secret
          </button>
          <button className={styles.blue_btn} onClick={handleHelloWorldTabClick}>
            Reveal Secrets
          </button>
        </div>
      </div>

      {isSecretFormOpen && <SecretForm />}
      {isHelloWorldOpen && <HelloWorldComponent onClose={() => setHelloWorldOpen(false)} />}
    </div>
  );
};

export default Main;
