import React, { useState } from 'react';
import axios from 'axios';
import styles from './styles.module.css'; // Import the styles

const SecretForm = () => {
    const [secret, setSecret] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = 'http://localhost:8080/submit-secret';
    
        try {
            const response = await fetch(url, {
                method: 'POST',
                
                body: JSON.stringify({ secretMessage: secret })
            });
    
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
    
            const result = await response.json();
            console.log(result);
            // Handle success (e.g., showing a success message)
        } catch (error) {
            console.error('There was an error!', error);
            // Handle error (e.g., showing an error message)
        }
    };
    

    return (
        <form onSubmit={handleSubmit} className={styles.secretForm}>
            <textarea 
                className={styles.secretTextarea}
                value={secret}
                onChange={(e) => setSecret(e.target.value)}
                placeholder="Share your secret"
            />
            <button type="submit" className={styles.submitButton}>Submit</button>
        </form>
    );
};

export default SecretForm;
