import React, { useState } from "react";
import styles from "./styles.module.css";
// import FormModal from "../FormModal";
import HelloWorldComponent from "../HelloWorldComponent"; // Import the HelloWorldComponent

const Main = () => {
  const [isFormModalOpen, setFormModalOpen] = useState(false);
  const [isHelloWorldOpen, setHelloWorldOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const handleFormTabClick = () => {
    setFormModalOpen(true);
    setHelloWorldOpen(false); // Close HelloWorldComponent when opening the form
  };

  const handleHelloWorldTabClick = () => {
    setHelloWorldOpen(!isHelloWorldOpen);
    setFormModalOpen(false); // Close FormModal when opening HelloWorldComponent
  };

  const closeFormModal = () => {
    setFormModalOpen(false);
  };

  return (
    <div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>SECRET.DO</h1>
        <button className={styles.white_btn} onClick={handleLogout}>
          Logout
        </button>
      </nav>
	  	<button className={styles.white_btn} onClick={handleFormTabClick}>
          Reveal Secrets
    	</button>
        <button className={styles.white_btn} onClick={handleHelloWorldTabClick}>
          Publish Secret
    	</button>
      {isHelloWorldOpen && <HelloWorldComponent onClose={handleHelloWorldTabClick} />}
    </div>
  );
};

export default Main;
