// export default SecretForm;

const SecretForm = () => {
    const [secret, setSecret] = useState('');
    const [error, setError] = useState(""); // Add an error state

    const handleSubmit = async (e) => {
        e.preventDefault();
       
try {
// Define the URL where your server is expecting to receive the POST request
const url = "http://localhost:8080/submit-secret";
// Make the POST request and await the resolved promise, storing the response
const { data: res } = await axios.post(url, { secretMessage: secret });

// Handle success (e.g., showing a success message)
console.log(res.message);
setSecret(''); // Clear the secret message input after successful submission
} catch (error) {
// Handle error (e.g., showing an error message)
// Set the error state to the error message received from the server or a default error message
setError(error.response?.data.message || "An error occurred while submitting your secret.");
}
};

return (
<form onSubmit={handleSubmit} className={styles.secretForm}>
<textarea
    className={styles.secretTextarea}
    value={secret}
    onChange={(e) => setSecret(e.target.value)}
    placeholder="Share your secret"
/>
<button type="submit" className={styles.submitButton}>Submit</button>
{error && <div className={styles.errorMessage}>{error}</div>}
</form>
);
};

export default SecretForm;
